import requests
import base64
from requests.auth import HTTPBasicAuth


def request_reporting_api(simpl_invoice_xml, uuid):
    url = "https://gw-fatoora.zatca.gov.sa/e-invoicing/developer-portal/invoices/reporting/single"
    username = ""
    password = "Xlj15LyMCgSC66ObnEO/qVPfhSbs3kDTjWnGheYhfSs="
    encoded_xml = base64.b64encode(simpl_invoice_xml.encode()).decode()

    # TODO: send invoice hash
    payload = {
        "invoiceHash": "zTUDjwJjoMOHqF3+J6IYHbJWXvLjLqMj9Gh3peYOn0s=",
        "uuid": uuid,
        "invoice": encoded_xml
    }
    headers = {
        "accept": "application/json",
        "Accept-Language": "en",
        "Accept-Version": "V2",
        "Content-Type": "application/json",
    }

    try:
        response = requests.post(url, json=payload, auth=HTTPBasicAuth(username, password), headers=headers)
        return response
    except Exception as e:
        return str(e)


