css:
 @page {
     margin: 160px 30px 70px 30px;
} 
body {
    font-size: 11px;
     font-family: Arial, sans-serif;
     color: #000;
}
 table { color: #000; }
.page-header {
     padding-bottom: 0 !important; 
     margin: 0 !important; 
     border-bottom: none !important;
     padding-top: 10px !important;
}
    .company-header {text-align: center; color: #000; margin-bottom: 20px;}
    .company-header h1 { margin: 0; font-size: 13px; font-weight: bold; text-transform: uppercase; }
    .company-header h2 { margin: 2px 0; font-size: 12px;  }
    .invoice-container {
       font-size: 11px;
    }
    table {
        border-collapse: collapse;
         font-size: inherit;
    }
    th, td {
        border: 1px solid #ddd;
        vertical-align: top;
        padding: 5px;
        font-weight: normal;
    }
    .print-format td, .print-format th {
         padding: 8px !important;
         color: #000;
    }
    .company-header {
        font-weight: 600;
        text-align: center;
    }
    .details-table strong {
        display: inline-block;
        width: 120px;
    }
.bank-details-table td{
    border: 0;
    padding: 0 !important;
    line-height: 1.2;
    break-inside: avoid;
}
.signatures-table {
    width: 100%;
    break-inside: avoid;
}
.signatures-table td{
    padding: 5px !important;
}
.detail-row {
    display: flex;
}
.detail-row span {
    width: 90px;
    display: block;
}
.footer {
    text-align: center; border-top: 1px solid #ddd;
    vertical-align: bottom;
    display: flex;
    justify-content: flex-end;
    height: 70px;
}
.ql-editor.read-mode {
    line-height: 1.2;
    color: #000;
}
.text-right {
    text-align: right;
}
.only-print {display: none}
@media print {
    .only-print {
        display: block;
    }
}

.items-table {
    min-height: 500px;
}
.items-table tr.item-last-row td {

    height: 100%;
}
