css:
 @page {
     margin: 160px 30px 70px 30px;
} 
body {
    font-size: 11px;
     font-family: Arial, sans-serif;
     color: #000;
}
    /* Layout & General */
    .invoice-container {
        font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
        color: #333;
        line-height: 1.4;
    }

    .page-header {
        margin-bottom: 20px;
    }

    /* Titles */
    .invoice-title-box {
        text-align: center;
        border-bottom: 2px solid #333;
        margin-bottom: 15px;
        padding-bottom: 5px;
    }

    .invoice-title-box h2 {
        margin: 0;
        font-size: 20px;
        text-transform: uppercase;
    }

    .vat-header {
        text-align: center;
        font-weight: bold;
        font-size: 14px;
    }

    /* Column System */
    .details-section {
        display: flex;
        justify-content: space-between;
        border: 1px solid #ddd;
        margin-top: 10px;
    }

    .column {
        flex: 1;
        padding: 10px;
    }

    .column-left {
        border-right: 1px solid #ddd;
    }

    .section-label {
        background-color: #f8f9fa;
        font-weight: bold;
        padding: 5px 10px;
        border: 1px solid #ddd;
        display: flex;
        justify-content: space-between;
    }

    .detail-row {
        margin-bottom: 4px;
        font-size: 11px;
    }

    /* Table Styling */
    .items-table {
        width: 100%;
        border-collapse: collapse;
        margin-top: 20px;
        font-size: 10px;
    }

    .items-table th {
        background-color: #f8f9fa;
        border: 1px solid #ddd;
        padding: 8px;
        text-align: center;
    }

    .items-table td {
        border: 1px solid #ddd;
        padding: 6px;
        vertical-align: top;
    }
.items-table {
    min-height: 200px;
}
.items-table tr.item-last-row td {

    height: 100%;
}
    .text-right { text-align: right; }
    .text-center { text-align: center; }

    /* Totals */
    .totals-container {
        display: flex;
        justify-content: space-between;
        margin-top: 15px;
    }

    .words-section {
        width: 55%;
        font-size: 10px;
        padding-top: 10px;
    }

    .totals-table {
        width: 40%;
        border-collapse: collapse;
    }

    .totals-table td {
        padding: 4px 8px;
        font-size: 11px;
    }

    .grand-total-row {
        font-weight: bold;
        background-color: #f8f9fa;
        font-size: 13px !important;
    }

    /* Footer & Signatures */
    .footer-section {
        width: 100%;
        margin-top: 30px;
        border-top: 1px solid #ddd;
        padding-top: 10px;
    }

    .signature-box {
        display: flex;
        justify-content: space-between;
        margin-top: 40px;
        text-align: center;
        font-size: 11px;
    }

    .qr-code {
        display: block;
        margin-left: auto;
    }

    .bank-info {
        font-size: 10px;
    }

    .only-print {display: none}
    @media print {
        .only-print {
            display: block;
        }
    }
